import React, { useEffect, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";

const Index = () => {
  const [swiperReady, setSwiperReady] = useState(false);

  useEffect(() => {
    // Delay Swiper initialization until component is mounted
    const timer = setTimeout(() => setSwiperReady(true), 0);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="home h-screen">
      <div className="homeContainer h-full">
        {swiperReady && (
          <Swiper
            spaceBetween={20}
            slidesPerView={1}
            onSlideChange={() => console.log("slide change")}
            onSwiper={(swiper) => console.log(swiper)}
            className="h-full w-full"
          >
            <SwiperSlide className="flex items-center justify-center text-white text-lg bg-blue-500">
              Slide 1
            </SwiperSlide>
            <SwiperSlide className="flex items-center justify-center text-white text-lg bg-red-500">
              Slide 2
            </SwiperSlide>
            <SwiperSlide className="flex items-center justify-center text-white text-lg bg-green-500">
              Slide 3
            </SwiperSlide>
            <SwiperSlide className="flex items-center justify-center text-white text-lg bg-yellow-500">
              Slide 4
            </SwiperSlide>
          </Swiper>
        )}
      </div>
    </div>
  );
};

export default Index;

