import React from "react";

const ThankYouEmail = () => {
  const name = "John Doe";
  const email = "john.doe@example.com";
  const phone = "+1-234-567-890";
  const subject = "Inquiry about product features";
  const body =
    "I would like to know more about the features of your new product.";

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        color: "#333",
        lineHeight: "1.6",
        padding: "20px",
      }}
    >
      <p>
        Hi <strong>{name}</strong>,
      </p>
      <p>
        Thank you for reaching out to us. We have received your message
        regarding "<strong>{subject}</strong>" and will get back to you as soon
        as possible.
      </p>
      <p>Below is a summary of your message:</p>
      <ul style={{ listStyleType: "none", paddingLeft: "0" }}>
        <li>
          <strong>Name:</strong> {name}
        </li>
        <li>
          <strong>Email:</strong> {email}
        </li>
        <li>
          <strong>Phone:</strong> {phone}
        </li>
        <li>
          <strong>Subject:</strong> {subject}
        </li>
        <li>
          <strong>Message:</strong> {body}
        </li>
      </ul>
      <p>Best Regards,</p>
      <p>
        <strong>Your Company Name</strong>
      </p>
    </div>
  );
};

export default ThankYouEmail;
