import { Button, Stack, TextField, Typography, colors } from "@mui/material";
import React from "react";
import { ScreenMode } from "../pages/SigninPage";

const SigninForm = ({ onSwitchMode }) => {
  return (
    <Stack
      justifyContent="center"
      alignItems="center"
      sx={{
        height: "100vh", // Full viewport height to center vertically
        backgroundColor: colors.grey[100], // Optional: adds a light background for contrast
        color: colors.grey[800],
        p: 2, // Optional: add padding around the container
      }}
    >
      <Stack
        spacing={3}
        sx={{
          width: "100%",
          maxWidth: "400px", // Reduced width for a smaller form
          p: 3, // Add padding inside the form container
          backgroundColor: "white", // Optional: adds a white background for the form
          borderRadius: 2, // Optional: adds rounded corners
          boxShadow: 1, // Optional: adds a subtle shadow
        }}
      >
        <Stack spacing={2} alignItems="center">
          <Typography variant="h5" fontWeight={600} color={colors.grey[800]}>
            Welcome back
          </Typography>
          <Typography color={colors.grey[600]} textAlign="center">
            Lorem ipsum dolor sit amet consectetur adipisicing elit
          </Typography>
        </Stack>

        <Stack spacing={3}>
          <Stack spacing={1}>
            <Typography color={colors.grey[800]}>Email</Typography>
            <TextField
              size="small"
              fullWidth
              variant="outlined"
              sx={{ bgcolor: colors.grey[50] }} // Optional: adds a light background to the input
            />
          </Stack>
          <Stack spacing={1}>
            <Typography color={colors.grey[800]}>Password</Typography>
            <TextField
              type="password"
              size="small"
              fullWidth
              variant="outlined"
              sx={{ bgcolor: colors.grey[50] }} // Optional: adds a light background to the input
            />
          </Stack>
          <Button
            variant="contained"
            size="large"
            sx={{
              bgcolor: colors.grey[800],
              "&:hover": {
                bgcolor: colors.grey[600],
              },
            }}
          >
            Sign in
          </Button>
        </Stack>

        <Stack direction="row" spacing={1} justifyContent="center">
          <Typography>Don't have an account?</Typography>
          <Typography
            onClick={() => onSwitchMode(ScreenMode.SIGN_UP)}
            fontWeight={600}
            sx={{
              cursor: "pointer",
              userSelect: "none",
            }}
          >
            Sign up now
          </Typography>
        </Stack>
      </Stack>
    </Stack>
  );
};

export default SigninForm;
