import React from "react";
import StadiumIcon from "@mui/icons-material/Stadium";
import EmojiEventsIcon from "@mui/icons-material/EmojiEvents";
import TheatersIcon from "@mui/icons-material/Theaters";
import "./Widget.scss";
import filmstripImage from "../../../assets/images/filmstrip.jpg";
const Widgets = ({ type }) => {
  let data;
  switch (type) {
    case "Festival":
      data = {
        title: "Upcoming Film Festivals",
        subtitle: "Join Now",
        icon: (
          <StadiumIcon
            classname="icon"
            style={{
              color: "white",
            }}
          />
        ),
      };
      break;
    case "filmAward":
      data = {
        title: "Award Winning Films",
        subtitle: "Watch Now",
        icon: (
          <EmojiEventsIcon
            classname="icon"
            style={{
              color: "white",
            }}
          />
        ),
      };
      break;
    case "unmissable":
      data = {
        title: "Unmissable Movies",
        subtitle: "Get Ticket",
        icon: (
          <TheatersIcon
            className="icon"
            style={{
              color: "white",
            }}
          />
        ),
      };
      break;
    default:
      break;
  }
  return (
    <div className="widget mx-2  flex justify-between p-3 " style={{}}>
      <img
        src={filmstripImage}
        className="absolute inset-0 w-full h-full z-10 object-cover z-0 opacity-50"
        alt="Filmstrip"
      />
      <div className=" bg-red-500">
        <h1>Hi</h1>
      </div>
      <div className="bg-purple-600">
        <h1>Hello</h1>
      </div>
    </div>
  );
};

export default Widgets;
