import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";
import "swiper/css/autoplay"; // This import can be removed if you're including Autoplay in the module imports

import {
  Navigation,
  Pagination,
  Scrollbar,
  A11y,
  Autoplay,
} from "swiper/modules";

import Navbar from "../../../components/customer/Navbar/Navbar";
import Footer from "../../../components/customer/Footer/Footer";

const Index = () => {
  return (
    <div className="home">
      <div className="homeContainer">
        <Navbar />
        <Swiper
          spaceBetween={50}
          slidesPerView={1}
          modules={[Navigation, Pagination, Scrollbar, A11y, Autoplay]}
          autoplay={{
            delay: 5000, // Time between slides in ms
            disableOnInteraction: false, // Continue autoplay after user interactions
          }}
          onSlideChange={() => console.log("slide change")}
          onSwiper={(swiper) => console.log(swiper)}
          className="h-screen w-screen"
        >
          <SwiperSlide className="text-center">
            <img
              src="/images/dp.webp"
              alt=""
              className="h-screen w-screen object-cover"
            />
          </SwiperSlide>
          <SwiperSlide className="text-center">
            {" "}
            <img
              src="/images/dp.webp"
              alt=""
              className="h-screen w-screen object-cover"
            />
          </SwiperSlide>
          <SwiperSlide className="text-center">Slide 3</SwiperSlide>
          <SwiperSlide className="text-center">Slide 4</SwiperSlide>
        </Swiper>
        {/* 
        <div className="h-screen">
           
        </div> */}
      </div>
    </div>
  );
};

export default Index;
