import React from "react";
import StadiumIcon from "@mui/icons-material/Stadium";
import EmojiEventsIcon from "@mui/icons-material/EmojiEvents";
import TheatersIcon from "@mui/icons-material/Theaters";
import "./Widget.scss";
import filmstripImage from "../../../assets/images/filmstrip.jpg";
const Widgets = ({ type }) => {
  let data;
  switch (type) {
    case "Festival":
      data = {
        title: "Upcoming Film Festivals",
        subtitle: "Join Now",
        icon: (
          <StadiumIcon
            classname="icon"
            style={{
              color: "white",
            }}
          />
        ),
      };
      break;
    case "filmAward":
      data = {
        title: "Award Winning Films",
        subtitle: "Watch Now",
        icon: (
          <EmojiEventsIcon
            classname="icon"
            style={{
              color: "white",
            }}
          />
        ),
      };
      break;
    case "unmissable":
      data = {
        title: "Unmissable Movies",
        subtitle: "Get Ticket",
        icon: (
          <TheatersIcon
            className="icon"
            style={{
              color: "white",
            }}
          />
        ),
      };
      break;
    default:
      break;
  }
  return (
    <div
      className="widget mx-2  flex justify-between p-3 "
      style={{
        backgroundImage: `url(${filmstripImage})`,
        backgroundColor: "rgba(0, 0, 0, 0.5)", // Red color with some transparency
        backgroundSize: "cover",
        backgroundBlendMode: "multiply", // Blend the background color and image
        transform: "rotate(15deg)", // Rotate the background image
      }}
    >
      <div className=" bg-red-500">
        <h1>Hi</h1>
      </div>
      <div className="bg-purple-600">
        <h1>Hello</h1>
      </div>
    </div>
  );
};

export default Widgets;
